mapper.select:
select count(*)
	from tbl_member
where mid=#{mid}

mapper.update:
UPDATE tbl_member
SET mname = #{mname}, memail = #{memail}, mphone=#{mphone}, maddress1 = #{maddress1}, maddress2 = #{maddress2}, maddress3 = #{maddress3}, mbirth = #{mbirth}, mmoney = #{mmoney}
WHERE mid = #{mid} AND mpw = #{mpw}
